mapper.select:
select *
from cart
where dish_id = #{dishId}
and user_id = #{userId}

mapper.update:
update cart
set dish_number = #{changeNumber}
where dish_id = #{dishId}
and user_id = #{userId}

update cart
set dish_number = dish_number + #{oneNumber}
where dish_id = #{dishId}
and user_id = #{userId}

mapper.delete:
delete
from cart
where dish_id = #{dishId}
and user_id = #{userId}

mapper.update:
update cart
set price = #{price}
where dish_id = #{dishId}
and user_id = #{userId}
